import React from 'react';
import { Button } from '../ButtonElements';
// import { NavLogo } from '../Navbar/NavbarElements';
import {
    InfoContainer, 
    InfoWrapper, 
    InfoRow, 
    Column1, 
    Column2, 
    TextWrapper,
    // TopLine, 
    InfoH1,
    Heading, 
    Subtitle,
    BtnWrap,
    ImgWrap,
    Img,
    ImgLogo
} from './infoElements';

const InfoSection = ({
    lightBg,
    id, 
    imgStart, 
    // topline, 
    lightText, 
    // headline, 
    darkText, 
    description, 
    buttonlabel, 
    img, 
    alt,
    primary,
    dark,
    dark2
}) => {
    return (
        <>
            <InfoContainer lightBg={lightBg} id={id}>
                <InfoH1>Portfolio</InfoH1>
                <InfoWrapper>
                    <InfoRow imgStart={imgStart}>
                        <Column1>
                            <TextWrapper>
                                {/* <TopLine>{topline}</TopLine> */}
                                <Heading lightText={lightText}>
                                    <ImgLogo src={require("../../images/logo.png")}/>
                                </Heading>
                                <Subtitle darkText={darkText}>{description}</Subtitle>
                                <BtnWrap>
                                    <Button to='https://cryptopresis.simlabs.io/'
                                        smooth={true}
                                        duration={500}
                                        spy={true}
                                        exact="true"
                                        offset={-80}
                                        primary={primary ? 1 : 0}
                                        dark={dark ? 1 : 0}
                                        dark2={dark2 ? 1 : 0}
                                        >{buttonlabel}</Button>
                                </BtnWrap>
                            </TextWrapper>
                        </Column1>
                        <Column2>
                            <ImgWrap>
                                <Img src={img} alt={alt}/>
                            </ImgWrap>
                        </Column2>
                    </InfoRow>
                </InfoWrapper>
            </InfoContainer>
        </>
    )
}

export default InfoSection
